import React, {useContext, useEffect,useState} from 'react'
import  {GlobalState} from '../../../GlobalState'
import BlogItem from './../utils/blogItem/BlogItem'
import axios from 'axios'


function Blogs() {
  const state = useContext(GlobalState)
  const [blogs, setBlogs] = state.blogsAPI.blogs
  const [isAdmin] = state.userAPI.isAdmin
  const [isCheck, setIsCheck] = useState(false)

  const handleCheck = (id) =>{
    blogs.forEach(blog => {
        if(blog._id === id) blog.checked = !blog.checked
    })
    setBlogs([...blogs])
  }

  const checkAll = () =>{
    blogs.forEach(blog =>{
      blog.checked = !isCheck
    })
    setBlogs([...blogs])
    setIsCheck(!isCheck)
  }
  useEffect(() => {
    
      getBlogs()
  }, [])
  
  return (
    <>
      {
        isAdmin && 
        <div className="delete-all">
          <span>Select all</span>
          <input type="checkbox" checked={isCheck} onChange={checkAll} />
        </div>
      }
      <div className="blogs">  
          {
              blogs.map(blog => {
                      return <BlogItem key={blog._id} blog={blog} isAdmin={isAdmin} handleCheck={handleCheck}/>
              })
          }
      </div>    
    </>
  )
}

export default Blogs
