import {useState} from 'react'
import axios from 'axios'

function BlogsAPI() {
    const [blogs, setBlogs] = useState([])

    useEffect(() => {
        const getBlogs  = async () => {
          const res  = await axios.get('/api/blog')
          setBlogs(res.data)
        }
        getBlogs()
      }, [setBlogs])

    return {
        blogs: [blogs, setBlogs]
    }
}

export default BlogsAPI
