import React, {useState, useContext} from 'react'
import axios from 'axios'
import {GlobalState} from '../../../GlobalState'
import Loading from '../utils/loading/Loading'

const initialState = {
    blog_id: '',
    title: '',
    description: ''
}

function CreateBlog() {
  const state = useContext(GlobalState)
  const [blog, setBlog] = useState(initialState)
  const [images, setImages] = useState(false)
  const [loading, setLoading] = useState(false)
  const [isAdmin] = state.userAPI.isAdmin

    const handleUpload = async e =>{
        e.preventDefault()
        try {
            if(!isAdmin) return alert("You're not an admin")
            const file = e.target.files[0]
            
            if(!file) return alert("File not exist")

            if(file.size > 1024 * 1024) // 1mb
                return alert("Size too large!")

            if(file.type !== 'image/jpeg' && file.type !== 'image/png') 
                return alert("File format is incorrect.")

            let formData = new FormData()
            formData.append('file', file)

            setLoading(true)
            c

        } catch (err) { 
            alert(err.response.data.msg)
        }
    }

  const styleUpLoad = {
      display: images ? "block" : "none"
  }

  return (
    <div className="create_blog">
        <div className="upload">
            <input type="file" name="file" id="file_up" onChange={handleUpload}/>
            <div className="blog_img" style={styleUpLoad}>
                <img src="" alt="" />
                <span>X</span>
            </div>
        </div>

        <form>
            <div className="row">
                <label htmlFor="blog_id">Blog ID</label>
                <input type="text" name="blog_id" id="blog_id" required
                value={blog.blog_id} />
            </div>

            <div className="row">
                <label htmlFor="title">Title</label>
                <input type="text" name="title" id="title" required
                value={blog.title} />
            </div>

            <div className="row">
                <label htmlFor="description">Description</label>
                <textarea type="text" name="description" id="decription" required
                value={blog.title} rows="10"/>
            </div>

            <button type="submit">Create</button>
        </form>
    </div>
  )
}

export default CreateBlog
