import React, { useContext, useState } from "react"
import { GlobalState } from '../../GlobalState'
import Menu from './icon/bars-solid.svg'
import Close from './icon/xmark-solid.svg'
import Cart from './icon/cart-shopping-solid.svg'
import { Link } from 'react-router-dom'
import axios from 'axios'
import Logo from '../../assets/logo.png'

function Header() {
    const state = useContext(GlobalState)
    const [isLogged] = state.userAPI.isLogged
    const [isAdmin] = state.userAPI.isAdmin
    const [cart] = state.userAPI.cart
    const [menu, setMenu] = useState(false)

    const logoutUser = async () => {
        await axios.get('/user/logout')

        localStorage.removeItem('firstLogin')

        window.location.href = "/"
    }

    // const adminRouter = () => {
    //     return (
    //         <>
    //             <li><Link to="/create_product">Create Product</Link></li>
    //             <li><Link to="/category">Categories</Link></li>
    //             <li><Link to="/blog">Blog</Link></li>
    //             <li><Link to="/create_blog">Create Blog</Link></li>
    //         </>
    //     )
    // }

    const loggedRouter = () => {
        return (
            <li><Link to="/" onClick={logoutUser}>Logout</Link></li>
        )
    }

    const styleMenu = {
        left: menu ? 0 : "-100%"
    }

    return (
        <header>
            <div className="menu" onClick={() => setMenu(!menu)}>
                <img src={Menu} alt="" width="30" />
            </div>
            <div className="logo">
                <h1>
                    <Link to="/"><img src={Logo} alt="Hong Trang Jewelry" width="150px" /></Link>
                </h1>
            </div>

            <ul style={styleMenu}>
                {isAdmin ? (
                    ''
                ) : (
                    <>
                        <li><Link to='/'>Home</Link></li>
                        <li><Link to='/about'>About us</Link></li>
                        <li><Link to='/products'>Products</Link></li>
                        <li><Link to='/blog'>Blog</Link></li>
                        <li><Link to='/*'>Contact</Link></li>
                    </>
                )}

                {isLogged ? (
                    loggedRouter()
                ) : (
                    <li><Link to="/login">Login/Register</Link></li>
                )}

                {/* {isAdmin && adminRouter()}
                {
                    isLogged ? loggedRouter() : <li><Link to="/login">Login/Register</Link></li>
                }

                <li onClick={() => setMenu(!menu)}>
                    <img src={Close} alt="" width="30" className="menu" />
                </li> */}

            </ul>

            {
                isAdmin ? ''
                    : <div className="cart-icon">
                        <span>{cart.length}</span>
                        <Link to="/cart">
                            <img src={Cart} alt="" width="30" />
                        </Link>
                    </div>
            }

        </header>
    )
}

export default Header
