import React, { useContext, useEffect, useState } from 'react'
import { GlobalState } from '../../../GlobalState'
import { Link, useParams, useHistory } from 'react-router-dom'
import axios from 'axios'



function User() {
    const state = useContext(GlobalState);
    const [user, setUser] = state.userAPI.user;
    const [history, setHistory] = state.userAPI.history;
    const [token] = state.token;
    const [callback, setCallback] = state.userAPI.callback;
    const [onEdit, setOnEdit] = useState(false);
    const params = useParams();
    const histories = useHistory();

    useEffect(() => {
        if (token) {
            const getUser = async () => {
                const res = await axios.get("/user/infor", {
                    headers: { Authorization: token }
                })
                console.log(res.data);
                setUser(res.data)
            }
            getUser()
        }
    }, [token, setUser]);
    useEffect(() => {
        if (token) {
            const getHistory = async () => {
                const res = await axios.get("/user/history", {
                    headers: { Authorization: token }
                })
                setHistory(res.data)
            }
            getHistory()
        }
    }, [token, setHistory]);
    useEffect(() => {
        if (params.id === user._id) {
            setOnEdit(true)
        } else {
            setOnEdit(false)
        }
    }, [params.id, user])

    const handleChangInput = async (e) => {
        const { name, value } = e.target
        setUser({ ...user, [name]: value })
    }
    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            if (onEdit) {
                await axios.put(`user/infor/${user._id}`, {
                    ...user,
                }, {
                    headers: { Authorization: token }
                })
            }
            setCallback(!callback)
            histories.push("/user")
        } catch (err) {
            alert(err.response.data.msg)
        }
    }
    return (
        <div className="container">
            <div className="row justify-content-between">
                <div className="col-3">
                    <h3>Thông tin tài khoản</h3>
                    <div className="user-info">
                        <label htmlFor="email">Email: </label>
                        <input type="text" name="email" id="email" value={user.email} disabled={true} />
                        <label htmlFor="email">Tên tài khoản: </label>
                        <input type="text" name="name" id="name" required onChange={handleChangInput} value={user.name} disabled={!onEdit} />

                    </div>
                </div>
                <div className="col-8">
                    <h3>Lịch sử mua hàng</h3>
                </div>
            </div>
        </div>
    )
}

export default User
