import React, {useContext, useState, useEffect} from 'react'
import {useParams, Link} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import BlogItem from '../utils/blogItem/BlogItem'

function DetailBlog() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [blogs] = state.blogsAPI.blogs
    const[detailBlog, setDetailBlog] = useState([])

    useEffect(()=>{
        if(params){
            blogs.forEach(blog => {
                if(blog._id === params.id) setDetailBlog(blog)
            })
        }
    },[params, blogs])

    if(detailBlog.length === 0) return null

    return (
    <>
        <div className="row detail_blog">
            <div className="box_detail col-lg-9">
                <h2>{detailBlog.title}</h2>
                <img src={detailBlog.images.url} alt="" />
                <p>{detailBlog.description}</p>
            </div>
            <div className="blog_list col-lg-3">
                <h3>Blog khác</h3>
                <div className="blog_list_item">
                    <img src={detailBlog.images.url} alt="" />
                    <h4>
                        {blogs.map(blog => {
                            <BlogItem b={blog.title} /> 
                        })}
                    </h4>
                </div>
            </div>
        </div>
    </>
    )
}

export default DetailBlog
