      const destroyImg = axios.post('/api/destroy', {public_id}, {
        headers: {Authorization: token}
      })
      const deleteBlog = axios.delete(`/api/blog/${blog._id}`, {
        headers: {Authorization: token}
      })

      await destroyImg
      await deleteBlog
      setLoading(false)
      setCallback(!callback)
    } catch (err) {
      alert(err.response.data.msg)
    }
  }
  
  
  return (
    <>
      {/* {
        isAdmin && 
