import React, {useState} from 'react'
import BtnRenderBlog from './BtnRenderBlog'
import axios from 'axios'
import Loading from '../loading/Loading'

function BlogItem({blog, isAdmin, token, callback, setCallback}) {
  const [loading, setLoading] = useState(false)

  const deleteBlogs = async() =>{
    try {
      const destroyImg = axios.post('/api/destroy', {public_id: blog.images.public_id}, {
        headers: {Authorization: token}
      })
      const deleteBlog = axios.delete(`/api/blog/${blog._id}`, {
        headers: {Authorization: token}
      })

      await destroyImg
      await deleteBlog
      setLoading(false)
      setCallback(!callback)
    } catch (err) {
      alert(err.response.data.msg)
    }
  }

  const handleCheck = () => {
    console.log(blog.checked)
  }

  if(loading) return <div className="blog_card"><Loading /></div>
  return (
    <div className="blog_card">
        {
          isAdmin && <input type="checkbox" checked={blog.checked} 
          onChange={() => handleCheck(blog._id)}/>
        }
        <img src={blog.images.url} alt="" />

        <div className="blog_box">
            <h2 title={blog.title}>{blog.title}</h2>
            <p>{blog.description}</p>
        </div>
        <BtnRenderBlog blog={blog}  deleteBlogs={deleteBlogs}/>
    </div>
  )
}

export default BlogItem
